import axios from 'axios';
import ReCAPTCHA from "react-google-recaptcha";
// import ReCAPTCHA from '../../ReCAPTCHA/ReCAPTCHA';
import React, {useState, useEffect} from 'react';
import {useNavigate} from 'react-router-dom';
import { useAsyncScript } from "react-async-script";
import './requests.css'

export default function Requests(props) {

    const [items, setItems] = useState([]);
    const [text, setText] = useState("");
    const [isVarified, setIsVarified] = useState(false);
    // function constructor(props){
    //     super(props);
    //     this.handleOnChange = this.handleOnChange.bind(this)
    // this.state = { items:[], text: "", isVarified: false }
    // }

    const handleOnChange = (value) => {
        console.log("Captcha Value:", value);
        // setText(event.target.value);
        setIsVarified(true);

    }

    const navigate = useNavigate();
    const [name, setName] = useState("")
    const [request, setRequest] = useState("");
    // const [token, setToken] = useState('');

    const RECAPTCHA_KEY = "6LfNNeYlAAAAAKxcz4Qwjw-dK8tZOGwY09RRHfbo";


    const handleReCAPTCHA = async () => {
        try {
          const token = await window.grecaptcha.enterprise.execute(
            RECAPTCHA_KEY,
            { action: 'user_input' }
          );
          setToken(token);
        } catch (error) {
          console.error(error);
        }
    };



// =====================================

    const handleSubmit = async(e) => {
        e.preventDefault();
        setIsVarified(false);
        setText("");

        const newRequest = {
            name,
            request
        };
        try{
            const response = await axios.post('/api/requests', newRequest);
                if(response.status){ //if response is true
                    // navigate('/')
                    console.log(response)

                } else { //if something is wrong with response
                    console.log("SOMETHING WENT WRONG!")
                    // console.err(err)
                }

        }catch (error){
            console.log(error)
            if (error.response) {
                // The request was made and the server responded with a status code
                console.log("Status code:", error.response.status);
                console.log("Response data:", error.response.data);
                console.log("Headers:", error.response.headers);
            } else if (error.request) {
                // The request was made but no response was received
                console.log("Request:", error.request);
            } else {
                // Something else happened while setting up the request
                console.log("Error:", error.message);
            }
        }
    }

//======================================
  return (
    <>

    <div className="fluidContainerRequests container-sm" id="requests">
        <div className="create">
            <h2>Have a Prayer request?</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Minus, vitae reiciendis? Impedit ad maiores tenetur enim eveniet earum officia neque repellat delectus voluptate reiciendis maxime, non dicta, saepe rerum velit.</p>
            <form id="add-request-form" action="" role="form" onSubmit={handleSubmit}>
                <label>Name</label>
                <input 
                    type="text" 
                    className="from-control" 
                    id="memberName" 
                    placeholder="Enter Name"
                    onChange={e=>setName(e.target.value)}
                />

                <label>Request Details</label>
                <textarea 
                    type="text" 
                    className="form-control" 
                    id="requestDetails" 
                    placeholder="Enter Request Details"
                    onChange={e=>setRequest(e.target.value)}

                >
                </textarea>

                <ReCAPTCHA 
                    sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                    onChange={handleOnChange}
                />

                <button 
                    type="submit" 
                    id="submit-request"
                    disabled={!isVarified}
                >
                submit prayer request
                </button>
            </form>
        </div>
    </div>
    </>
  )
}


